// Para archivos SVG
declare module "*.svg" {
  const content: string;
  export default content;
}

// Para archivos PNG (por si los necesitas)
declare module "*.png" {
  const content: string;
  export default content;
}

// Para archivos JPEG/JPG
declare module "*.jpg" {
  const content: string;
  export default content;
}

// Para archivos JPEG/JPG
declare module "*.jpeg" {
  const content: string;
  export default content;
}

declare module "*.svg?url" {
  const content: string;
  export default content;
}
